import React, { useState } from 'react';

const booksData = [
  {
    title: 'Il signore degli anelli',
    author: 'J.R.R. Tolkien',
    imageCover: 'data:image/jpeg;base64,',
    price: 20.99
  },
  {
    title: 'Il piccolo principe',
    author: 'Antoine de Saint-Exupéry',
    imageCover: 'data:image/jpeg;base64,',
    price: 12.99
  },
  {
    title: 'Harry Potter e la pietra filosofale',
    author: 'J.K. Rowling',
    imageCover: 'data:image/jpeg;base64,',
    price: 15.99
  },
  {
    title: '1984',
    author: 'George Orwell',
    imageCover: 'data:image/jpeg;base64,',
    price: 9.99
  },
  {
    title: 'Il signore degli anelli il ritorno del re',
    author: 'J.R.R. Tolkien',
    imageCover: 'data:image/jpeg;base64,',
    price: 25.99
  },
  {
    title: 'Harry Potter e la camera dei segreti',
    author: 'J.K. Rowling',
    imageCover: 'data:image/jpeg;base64,',
    price: 17.99
  },
  {
    title: '1984',
    author: 'Aldous Huxley',
    imageCover: 'data:image/jpeg;base64,',
    price: 11.99
  },
  {
    title: 'Il grande Gatsby',
    author: 'F. Scott Fitzgerald',
    imageCover: 'data:image/jpeg;base64,',
    price: 14.99
  }
];

function App() {
  const [searchTitle, setSearchTitle] = useState('');
  const [searchAuthor, setSearchAuthor] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  function handleSearch() {
    const results = booksData.filter(book => {
      const titleMatch = book.title.toLowerCase().includes(searchTitle.toLowerCase());
      const authorMatch = book.author.toLowerCase().includes(searchAuthor.toLowerCase());
      return titleMatch || authorMatch;
    });

    setSearchResults(results);
  }

  return (
    <div className="container mx-auto p-8">
      <Header />
      <Main
        searchTitle={searchTitle}
        setSearchTitle={setSearchTitle}
        searchAuthor={searchAuthor}
        setSearchAuthor={setSearchAuthor}
        handleSearch={handleSearch}
        searchResults={searchResults}
      />
      <Footer />
    </div>
  );
}

function Header() {
  return <h1 className="text-3xl font-bold mb-3">TuLibro - La tua libreria online</h1>;
}

function Main({ searchTitle, setSearchTitle, searchAuthor, setSearchAuthor, handleSearch, searchResults }) {
  return (
    <div>
      <div className="flex mb-4">
        <input
          type="text"
          value={searchTitle}
          onChange={(e) => setSearchTitle(e.target.value)}
          placeholder="Titolo"
          className="p-2 border border-gray-300 rounded-l focus:outline-none focus:border-blue-500"
        />
        <input
          type="text"
          value={searchAuthor}
          onChange={(e) => setSearchAuthor(e.target.value)}
          placeholder="Autore"
          className="p-2 border border-gray-300 rounded-r focus:outline-none focus:border-blue-500"
        />
        <button
          onClick={handleSearch}
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 ml-4 rounded"
        >
          Cerca
        </button>
      </div>
      <div>
        {searchResults.length === 0 ? (
          <p className="text-red-500">Nessun libro trovato</p>
        ) : (
          searchResults.map((book, index) => (
            <div key={index} className="flex items-center mb-4">
              <img src={book.imageCover} alt={book.title} className="w-24 h-32 mr-4" />
              <div>
                <p className="font-bold text-lg">{book.title}</p>
                <p className="text-gray-600">{book.author}</p>
                <p className="text-green-500 font-bold">{book.price} €</p>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

function Footer() {
  const currentYear = new Date().getFullYear();
  return (
    <p className="bg-slate-300 text-center text-sm mt-4">
      &copy; {currentYear} TuLibro - Tutti i diritti riservati.
    </p>
  );
}


export default App;
